import React, { useRef, useState, useEffect } from "react";
import Editor from "./editor/Editor";
import { useEditor, EditorContent } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";
import { useNavigate } from "react-router";
import db from "../firebase";
import { onSnapshot, collection, addDoc } from "firebase/firestore";
import { Form, Button } from "react-bootstrap";
import { doc, getDoc } from "firebase/firestore";
import { useSearchParams } from "react-router-dom";

function EditPost(props) {
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const [post, setPost] = useState({
    title: "Loading...",
    body: "Loading...",
    id: "initial",
  });

  const titleRef = useRef();
  const tagRef = useRef();

  const [serachParam, setSearchParam] = useSearchParams();
  serachParam.get("postId");

  let editor = null;
  const docRef = doc(db, "posts", serachParam.get("postId"));
  function initEditor() {
    editor = useEditor({
      extensions: [StarterKit],
      content: result.body,
    });
  }
  getDoc(docRef).then((result) => {
    if (result.exists()) {
      console.log("Document data:", result.data());
      setPost(result.data());
      initEditor();
    } else {
      // doc.data() will be undefined in this case
      console.log("No such document!");
    }
  });
  async function submitPost(title, body, tags) {
    const data = {
      body: body,
      tags: tags,
      title: title,
    };
    const collectionRef = collection(db, "posts");
    const docRef = await addDoc(collectionRef, data);
    console.log("The new ID is: " + docRef.id);
  }

  async function handlePost(e) {
    e.preventDefault();
    try {
      setError("");
      setLoading(true);
      await submitPost(
        titleRef.current.value,
        editor.getHTML(),
        tagRef.current.value
      );
      navigate("/blog");
    } catch {
      setError("Failed to submit post");
    }

    setLoading(false);
  }

  return (
    <div>
      <h2>Edit Post</h2>
      <Form.Control value={post.title} ref={titleRef} type="text" />
      <br />
      <Form.Control
        placeholder="Enter some tags..."
        value={post.tags}
        ref={tagRef}
        type="text"
      />
      <br />
      <Editor editor={editor} />
      <br />
      <Button disabled={loading} onClick={handlePost}>
        Post
      </Button>
    </div>
  );
}

export default EditPost;
