import React, { useRef, useState } from "react";
import Editor from "./editor/Editor";
import { useEditor, EditorContent } from "@tiptap/react";
import StarterKit from "@tiptap/starter-kit";
import { useNavigate } from "react-router";
import firebase from "@firebase/app-compat";
import db from "../firebase";
import { onSnapshot, collection, addDoc } from "firebase/firestore";

function CreatePost(props) {
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const editor = useEditor({
    extensions: [StarterKit],
    content: "",
  });

  const titleRef = useRef();
  const tagRef = useRef();

  async function submitPost(title, body, tags) {
    const data = {
      body: body,
      tags: tags,
      title: title,
    };
    // db.collection("posts")
    //   .add(data)
    //   .then((result) => {
    //     console.log("New document ID", result.id);
    //   });
    const collectionRef = collection(db, "posts");
    const docRef = await addDoc(collectionRef, data);
    console.log("The new ID is: " + docRef.id);
  }

  // const test = {
  //   body: "Testing a write",
  //   tags: "testing,a,write",
  //   title: "Testing a Write",
  // };

  // db.collection("posts")
  //   .add(test)
  //   .then((result) => {
  //     console.log("New document ID", result.id);
  //   });

  async function handlePost(e) {
    e.preventDefault();
    try {
      setError("");
      setLoading(true);
      await submitPost(
        titleRef.current.value,
        editor.getHTML(),
        tagRef.current.value
      );
      navigate("/blog");
    } catch {
      setError("Failed to submit post");
    }

    setLoading(false);
  }

  return (
    <div>
      <input ref={titleRef} type="text" />
      <input ref={tagRef} type="text" />
      <Editor editor={editor} />
      <button disabled={loading} onClick={handlePost}>
        Post
      </button>
    </div>
  );
}

export default CreatePost;
